const projext = require('projext/index');
/**
 * Implements the Webpack middlewares for a target on an Express app.
 * @param {Express} expressApp    The app where the middlewares are going to be `use`d.
 * @param {string}  targetToBuild The name of the target that will be builded on the middleware(s).
 * @param {string}  targetToServe The name of the target that will implement the middleware(s). When
 *                                the other target is builded, it will assume that is on the
 *                                distribution directory, and if the target serving it is being
 *                                executed from the source directory it won't be able to use the
 *                                dev middleware file system without hardcoding some relatives paths
 *                                from the build to the source; to avoid that, the method gets
 *                                the build path of this target, so when using `getDirectory()`, it
 *                                will think they are both on the distribution directory and the
 *                                paths can be created relative to that.
 * @return {MiddlewaresInformation}
 */
const useExpress = (expressApp, targetToBuild, targetToServe) => {
  // Get the middlewares service.
  const webpackMiddlewares = projext.get('webpackMiddlewares');
  // Generate the middlewares for the target.
  const info = webpackMiddlewares.generate(targetToBuild, targetToServe);
  // Loop all the received middlewares...
  info.middlewares.forEach((middleware) => {
    // ...and register them on the app.
    expressApp.use(middleware());
  });

  return info;
};

module.exports = useExpress;

const {
  webpackConfiguration,
  webpackBuildEngine,
} = require('./services/building');

const {
  webpackBaseConfiguration,
  webpackBrowserDevelopmentConfiguration,
  webpackBrowserProductionConfiguration,
  webpackRulesConfiguration,
  webpackNodeDevelopmentConfiguration,
  webpackNodeProductionConfiguration,
} = require('./services/configurations');

const {
  webpackMiddlewares,
} = require('./services/server');

const { name } = require('../package.json');
/**
 * This is the method called by projext when loading the plugin. It takes care of registering
 * the Webpack build engine service and all the other services the engine depends on.
 * @param {Projext} app The projext main container.
 * @ignore
 */
const loadPlugin = (app) => {
  /**
   * This define the basic information of the plugin for other services to use:
   * - The name of the plugin.
   * - Where the `webpack.config.js` file is located.
   * - The subpaths the plugin expose. Since they won't match a dependency on the `package.json`,
   *   a Node target may want to include them while bundling.
   */
  app.set('webpackPluginInfo', () => ({
    name,
    configuration: 'src/webpack.config.js',
    external: [
      'express',
      'jimpex',
    ],
  }));
  // Register the main services of the build engine.
  app.register(webpackConfiguration);
  app.register(webpackBuildEngine);

  // Register the services for building the targets configurations.
  app.register(webpackBaseConfiguration);
  app.register(webpackBrowserDevelopmentConfiguration);
  app.register(webpackBrowserProductionConfiguration);
  app.register(webpackRulesConfiguration);
  app.register(webpackNodeDevelopmentConfiguration);
  app.register(webpackNodeProductionConfiguration);

  // Register the service for server integration.
  app.register(webpackMiddlewares);
};

module.exports = loadPlugin;

const path = require('path');
const { provider } = require('jimple');
/**
 * This service overwrites the `Jimpex` default `FrontendFs` so it will use the file system
 * provided by the Webpack middleware instead of using the real file system.
 */
class WebpackFrontendFs {
  /**
   * Class constructor.
   * @param {DevMiddlewareGetDirectory}  getDirectory  A function to get the directory the
   *                                                   Webpack middleware is using.
   * @param {DevMiddlewareGetFileSystem} getFileSystem A function to get the virtual file system
   *                                                   the Webpack middleware uses. It uses a
   *                                                   Promise in order to avoid accessing it
   *                                                   before the middleware finishes compiling.
   */
  constructor(getDirectory, getFileSystem) {
    /**
     * A function to get the directory the Webpack middleware is using.
     * @type {DevMiddlewareGetDirectory}
     */
    this.getDirectory = getDirectory;
    /**
     * A function that returns a Promise with the virtual file system the Webpack middleware uses.
     * @type {DevMiddlewareGetFileSystem}
     */
    this.getFileSystem = getFileSystem;
  }
  /**
   * Read a file from the file system.
   * @param {string} filepath           The path to the file.
   * @param {string} [encoding='utf-8'] The text encoding in which the file should be read.
   * @return {Promise<string,Error>}
   */
  read(filepath, encoding = 'utf-8') {
    return this.getFileSystem()
    .then((fileSystem) => new Promise((resolve, reject) => {
      fileSystem.readFile(this._getPath(filepath), encoding, (error, data) => {
        if (error) {
          reject(error);
        } else {
          resolve(data);
        }
      });
    }));
  }
  /**
   * Write a file on the file system.
   * @param {string} filepath The path to the file.
   * @param {string} data     The contents of the file.
   * @return {Promise<undefined,Error>}
   */
  write(filepath, data) {
    return this.getFileSystem()
    .then((fileSystem) => new Promise((resolve, reject) => {
      fileSystem.writeFile(this._getPath(filepath), data, (error) => {
        if (error) {
          reject(error);
        } else {
          resolve();
        }
      });
    }));
  }
  /**
   * Delete a file from the file system.
   * @param {string} filepath The path to the file.
   * @return {Promise<undefined,Error>}
   */
  delete(filepath) {
    return this.getFileSystem()
    .then((fileSystem) => new Promise((resolve, reject) => {
      fileSystem.unlink(this._getPath(filepath), (error) => {
        if (error) {
          reject(error);
        } else {
          resolve();
        }
      });
    }));
  }
  /**
   * Generate a path using the virtual file system directory as root.
   * @param {string} rest The path you want to prefix with the file system directory.
   * @return {string}
   * @ignore
   * @access protected
   */
  _getPath(rest) {
    return path.join(this.getDirectory(), rest);
  }
}
/**
 * Generate a `Provider` with an already defined `getDirectory` and `getFileSystem` functions.
 * @example
 * // Generate the provider
 * const provider = webpackFrontendFs(() => 'some-dir', () => middleware.fs);
 * // Register it on the container
 * container.register(provider);
 * // Getting access to the service instance
 * const frontendFs = container.get('frontendFs');
 * @param {DevMiddlewareGetDirectory}  getDirectory  A function to get the directory the
 *                                                   Webpack middleware is using.
 * @param {DevMiddlewareGetFileSystem} getFileSystem A function to get the virtual file system
 *                                                   the Webpack middleware uses. It uses a
 *                                                   Promise in order to avoid accessing it
 *                                                   before the middleware finishes compiling.
 * @return {Provider}
 */
const webpackFrontendFs = (getDirectory, getFileSystem) => provider((app) => {
  app.set('frontendFs', () => new WebpackFrontendFs(
    getDirectory,
    getFileSystem
  ));
});

module.exports = {
  WebpackFrontendFs,
  webpackFrontendFs,
};

